(function($){$.fn.viewportState=(function(){var results=[];this.each(function(){var bounds=this.getBoundingClientRect();if($(window).height()<bounds.top){results.push(['below',$(this)]);}else if(bounds.bottom<=0){results.push(['above',$(this)]);}else{results.push(['screen',$(this)]);};});return results;});$.fn.inViewport=(function(){var results=[];this.each(function(){var bounds=this.getBoundingClientRect();if($(window).height()<bounds.top){}else if(bounds.bottom<=0){}else{results.push(this);};});return $(results);});$.fn.aboveViewport=(function(){var results=[];this.each(function(){var bounds=this.getBoundingClientRect();if($(window).height()<bounds.top){}else if(bounds.bottom<=0){results.push(this);}else{};});return $(results);});$.fn.belowViewport=(function(){var results=[];this.each(function(){var bounds=this.getBoundingClientRect();if($(window).height()<bounds.top){results.push(this);}else if(bounds.bottom<=0){}else{};});return $(results);});})(jQuery);